import base64

# The Base64 string (truncated for brevity in this example)
base64_data = "..."

# Decode the Base64 string
binary_data = base64.b64decode(base64_data)

# Write the binary data to a .woff file
with open("decoded_font.woff", "wb") as font_file:
    font_file.write(binary_data)

print("Font saved as decoded_font.woff")